mapper.insert:
/* boardInsert */

INSERT INTO
	BOARD
		(
			BOARD_NO
			, TITLE
			, CONTENT
			, BOARD_PW
			, BOARD_OPEN_YN
			, HITS
			, USE_YN
			, REG_ID
			, REG_DATE
		)
	VALUES
		(
			BOARD_SEQ.NEXTVAL
			, #{title}
			, #{content}
			, #{boardPw}
			, #{boardOpenYn}
			, 0
			, 'Y'
			, #{regId}
			, SYSDATE
		)

mapper.update:
/* boardUpdate */

UPDATE
	BOARD
SET
	TITLE = #{title}
	, CONTENT = #{content}
	, BOARD_PW = #{boardPw}
	, BOARD_OPEN_YN = #{boardOpenYn}
	, UPDT_ID = #{updtId}
	, UPDT_DATE = SYSDATE
WHERE		
	BOARD_NO = #{boardNo}

mapper.delete:
/* boardDelete */



UPDATE
	BOARD
SET
	USE_YN = 'N'
	, UPDT_ID = #{updtId}
	, UPDT_DATE = SYSDATE
WHERE		
	BOARD_NO = #{boardNo}

mapper.update:
/* boardHitsCntUpdate */

UPDATE
	BOARD
SET
	HITS = (SELECT NVL(MAX(HITS), 0) + 1 FROM BOARD WHERE BOARD_NO = #{boardNo})
	, UPDT_ID = #{updtId}
	, UPDT_DATE = SYSDATE
WHERE		
	BOARD_NO = #{boardNo}
